export class ChatComponent {
  chatOpen = false;
  isTyping = false;
  messages: { text: string, sender: string }[] = [];

  openChat() {
    this.chatOpen = true;
  }

  closeChat() {
    this.chatOpen = false;
  }

  sendMessage(input: HTMLInputElement) {
    const message = input.value;
    if (message.trim()) {
      this.messages.push({ text: message, sender: 'user' });
      input.value = '';
      this.simulateServerResponse();
    }
  }

  simulateServerResponse() {
    this.isTyping = true;
    setTimeout(() => {
      this.isTyping = false;
      this.messages.push({ text: 'Risposta del chatbot', sender: 'server' });
    }, 2000); // Simula una risposta dopo 2 secondi
  }

  onKeydown(event: KeyboardEvent, input: HTMLInputElement) {
    if (event.key === 'Enter') {
      this.sendMessage(input);
    }
  }
}
