import { Component, OnInit } from '@angular/core';
import { io } from 'socket.io-client';  // Importa io correttamente

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent implements OnInit {
  socket: any;
  message: string = '';
  messages: string[] = [];
  chatOpen: boolean = false;
  isTyping: boolean = false;  // Variabili per gestire lo stato di "scrittura" del server

  ngOnInit() {
    // Connessione al server Flask con Socket.IO (usa HTTPS)
    this.socket = io('https://friendly-train-x55w6wv65jxqfp5gg-5000.app.github.dev/');  // Cambia l'URL se necessario

    // Ascolta i messaggi ricevuti dal server (chatbot)
    this.socket.on('message', (msg: string) => {
      this.messages.push(`Piombobot: ${msg}`);
      this.isTyping = false;  // Nascondi l'indicatore di scrittura
    });
  }

  sendMessage(message: HTMLTextAreaElement) {
    this.message = message.value;
    if (this.message.trim()) {
      // Aggiungi il messaggio dell'utente alla chat
      this.messages.push(`Tu: ${this.message}`);
      message.value = '';  // Pulisce la casella di testo

      // Indica che il server sta scrivendo
      this.isTyping = true;

      // Invia il messaggio al server tramite Socket.IO
      this.socket.emit('message', this.message);
    }
  }

  // Metodo per aprire il popup della chat
  openChat(): void {
    this.chatOpen = true;
  }

  // Metodo per chiudere il popup della chat
  closeChat(): void {
    this.chatOpen = false;
  }

  // Metodo per gestire la pressione di un tasto (Enter)
  onKeydown(event: KeyboardEvent, message: HTMLTextAreaElement): void {
    if (event.key === 'Enter') {
      this.sendMessage(message);
    }
  }

  // Metodo per gestire l'altezza dinamica del campo di input
  adjustHeight(input: HTMLTextAreaElement) {
    input.style.height = 'auto';  // Reset height to auto first
    input.style.height = `${input.scrollHeight}px`;  // Set height based on content scrollHeight
  }
}
<div class="page-container">
  <!-- Chatbot Button -->
  <button class="open-chat-btn" (click)="openChat()">💬</button>

  <!-- Chatbot Popup -->
  <div *ngIf="chatOpen" class="chat-popup">
    <div class="chat-header">
      <img src="https://upload.wikimedia.org/wikipedia/commons/e/ed/Aldo_Moro_portrait.jpg" alt="Aldo Moro" class="chat-avatar" />
      <span class="chat-name">Piombobot</span>
      <button class="close-chat-btn" (click)="closeChat()">X</button>
    </div>
    <div class="chat-body">
      <div class="messages">
        <div *ngFor="let msg of messages" class="message" [ngClass]="{'user-message': msg.startsWith('Tu:'), 'bot-message': !msg.startsWith('Tu:')}">
          {{ msg }}
        </div>
        <!-- Typing indicator -->
        <div *ngIf="isTyping" class="server-typing">Server is typing...</div>
      </div>
      <textarea placeholder="Type a message..." #message (input)="adjustHeight(message)" (keydown)="onKeydown($event, message)"></textarea>
      <button (click)="sendMessage(message)">Send</button>
    </div>
  </div>
</div>
