import { Component, OnInit } from '@angular/core';
import { io } from 'socket.io-client';  // Importa io correttamente

@Component({
  selector: 'app-chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.css']
})
export class ChatComponent implements OnInit {
  socket: any;
  message: string = '';
  messages: string[] = [];
  chatOpen: boolean = false;
  isTyping: boolean = false;  // Variabile per gestire lo stato di "scrittura" del server

  ngOnInit() {
    // Connessione al server Flask con Socket.IO (usa HTTPS)
    this.socket = io('https://orange-halibut-5ggq6qx6g6gj277rv-5000.app.github.dev');  // Cambia l'URL se necessario

    // Ascolta i messaggi ricevuti dal server (chatbot)
    this.socket.on('message', (msg: string) => {
      this.messages.push(`Piombobot: ${msg}`);
      this.isTyping = false;  // Nascondi l'indicatore di scrittura
    });
  }

  sendMessage(message: HTMLInputElement) {
    this.message = message.value;
    if (this.message.trim()) {
      // Aggiungi il messaggio dell'utente alla chat
      this.messages.push(`Tu: ${this.message}`);
      message.value = '';  // Pulisce la casella di testo

      // Indica che il server sta scrivendo
      this.isTyping = true;

      // Invia il messaggio al server tramite Socket.IO
      this.socket.emit('message', this.message);
    }
  }

  // Metodo per aprire il popup della chat
  openChat(): void {
    this.chatOpen = true;
  }

  // Metodo per chiudere il popup della chat
  closeChat(): void {
    this.chatOpen = false;
  }

  // Metodo per gestire la pressione di un tasto (Enter)
  onKeydown(event: KeyboardEvent, message: HTMLInputElement): void {
    if (event.key === 'Enter') {
      this.sendMessage(message);
    }
  }
}
<div class="page-container">
  <h1 class="page-title">ANNI DI PIOMBO</h1>

  <!-- Chatbot Button -->
  <button class="open-chat-btn" (click)="openChat()">💬 Chat</button>

  <!-- Chatbot Popup -->
  <div *ngIf="chatOpen" class="chat-popup">
    <div class="chat-container">
      <div class="messages">
        <div *ngFor="let msg of messages">{{ msg }}</div>

        <!-- Aggiungi un indicatore di caricamento mentre il server "scrive" -->
        <div *ngIf="isTyping" class="server-typing">Server sta scrivendo...</div>
      </div>
      <input placeholder="Scrivi un messaggio..." #message (keydown)="onKeydown($event, message)" />
      <button (click)="sendMessage(message)">Invia</button>
    </div>
    <button class="close-chat-btn" (click)="closeChat()">X</button>
  </div>
</div>
